mapper.update:
update shopInfo 
set shopOpen=#{shopOpen}, shopClose=#{shopClose}, shopOff=#{shopOff}, shopIntro=#{shopIntro}, shopParking=#{shopParking}, shopPolicy=#{shopPolicy}

	,shopPhoto=#{shopPhoto}

where shopNum=#{shopNum} and memberId=#{memberId}

mapper.select:
select shopNum, memberId, shopName, shopPhone, shopAddr, shopOpen, shopClose, shopOff, shopPhoto, shopIntro, shopParking, shopPolicy
from shopInfo
where memberId=#{memberId}

	and shopNum=#{shopNum}

select shopNum, memberId, shopName, shopPhone, shopAddr, shopOpen, shopClose, shopOff, shopPhoto, shopIntro, shopParking, shopPolicy
from shopInfo
where shopNum=#{shopNum}
